export namespace IBbsArticle {
export interface IStore {
/**
* @minLength 3
* @maxLength 50
*/
title: string;
body: string;
files: IAttachmentFile[];
}
}

export interface IAttachmentFile {
/**
* @minLengt 1
* @maxLength 255
*/
name: string | null;

/**
* @minLength 1
* @maxLength 8
*/
extension: string | null;

/**
* @format url
*/
url: string;
}



